#!/usr/bin/python# -*- coding: utf-8 -*-import timeimport sysdef dec_to_bin(p):    binario = ""    while p >= 1:        binario = (binario + str(int(p % 2)))        p = p//2    binario = binario[::-1]    print(f'Binário do número digitado é {binario}\n')    time.sleep(3)    inicio()def dec_to_hex(q):    hexadecimal = ""    while q > 0:        if q % 16 < 10:            hexadecimal = hexadecimal + str((q % 16))            q = q // 16        elif q % 16 == 10:            hexadecimal = hexadecimal + 'A'            q = q // 16        elif q % 16 == 11:            hexadecimal = hexadecimal + 'B'            q = q // 16        elif q % 16 == 12:            hexadecimal = hexadecimal + 'C'            q = q // 16        elif q % 16 == 13:            hexadecimal = hexadecimal + 'D'            q = q // 16        elif q % 16 == 14:            hexadecimal = hexadecimal + 'E'            q = q // 16        elif q % 16 == 15:            hexadecimal = hexadecimal + 'F'            q = q // 16    hexadecimal = hexadecimal[::-1]    print(f'O hexadecimal do número digitado é {hexadecimal}\n')    time.sleep(3)    inicio()def bin_to_dec(n):    n = n[::-1]    tam = len(n)    decimal = 0    for num in range(tam):        if n[num] == "1":            decimal = decimal + (2**num)    print(f'O Decimal do binário digitado é {decimal}\n')    time.sleep(3)    inicio()def hex_to_dec(h):    h = h[::-1]    tam = len(h)    decimal = 0    for algo in range(tam):        h = str(h)        h = h.upper()        if h[algo] == 'A':            decimal = decimal + (10*(16**algo))        elif h[algo] == 'B':            decimal = decimal + (11*(16**algo))        elif h[algo] == 'C':            decimal = decimal + (12*(16**algo))        elif h[algo] == 'D':            decimal = decimal + (13*(16**algo))        elif h[algo] == 'E':            decimal = decimal + (14*(16**algo))        elif h[algo] == 'F':            decimal = decimal + (15*(16**algo))        else:            h1 = int(h[algo])            resultado = h1 * (16 ** algo)            decimal = decimal + resultado    print(f'O hexadecimal convertido em decimal é {decimal}\n')    time.sleep(3)    inicio()def inicio():    while True:        print('[ 1 ] - Decimal para Binário')        print('[ 2 ] - Decimal para Hexadecimal')        print('[ 3 ] - Decimal para Octal [Em Desenvolvimento]')        print('[ 4 ] - Binário para Decimal')        print('[ 5 ] - Hexadecimal para Decimal')        print('[ 0 ] - Sair')        try:            opcao = int(input('Escolha uma opção: '))            if opcao == 1:                p = int(input('Digite o número a ser convertido: '))                dec_to_bin(p)            elif opcao == 2:                q = int(input('Digite o número a ser convertido: '))                dec_to_hex(q)            elif opcao == 4:                n = input('Digite o binário a ser convertido: ')                bin_to_dec(n)            elif opcao == 5:                h = input('Digite o hexadecimal a ser convertido: ')                hex_to_dec(h)            elif opcao == 0:                print('Saindo....')                time.sleep(3)                sys.exit()        except ValueError:            print('Por Favor, digite apenas números!')        else:            print('Digite uma opção válida...')inicio()